<div class="content">        <div class="content__body">            <div class="content__list">                <?php if(!empty($this->arrCurrentCompetitions)): ?>                <?php for($i = 0; $i < count($this->arrCurrentCompetitions); $i++): ?>                    <div class="content__item">                        <div class="content__item-title">                            <?= $this->arrCurrentCompetitions[$i]['name'] ?>                        </div>                        <div class="content__item-date">                            <?= $this->arrCurrentCompetitions[$i]['date'] ?> <?= $this->arrCurrentCompetitions[$i]['time'] ?>                        </div>                        <div class="content__item-block">                            <div class="content__item-img">                                <picture><source srcset="<?= $this->arrCurrentCompetitions[$i]['url'] ?>" type="image/webp"><img src="<?= $this->arrCurrentCompetitions[$i]['url'] ?>" alt=""></picture>                            </div>                            <div class="content__item-row">                                <div class="content__item-description">                                    <?= $this->arrCurrentCompetitions[$i]['description'] ?>                                </div>                                <div class="button">                                    <a href="#popup-result<?= $this->arrCurrentCompetitions[$i]['id_competition'] ?>">Посмотреть сетку</a>                                </div>                                <div class="button">                                    <?php if(!isset($_COOKIE['admin']) ): ?>                                        <?php if(isset($_COOKIE['user'])): ?>                                        <?php for($j = 0; $j < count($this->arrPopupResult); $j++): $fl = true;?>                                        <?php if($this->arrCurrentCompetitions[$i]['id_competition'] == $this->arrPopupResult[$j]['id'] && $this->arrPopupResult[$j]['id_athlete'] == $this->infoAboutCurrentAthlete[0]['id_athlete']): $fl = false;?>                                        <?php break; endif; ?>                                        <?php endfor; ?>                                        <?php if($fl): ?>                                                <form action="" method="post">                                                    <input type="submit" value="Подать заявку для выступления" name="new-questionnaire" class="input-new-questionnaire">                                                    <input type="hidden" value="<?= $this->arrCurrentCompetitions[$i]['id_competition'] ?>" name="id_competition">                                                </form>                                        <?php endif; ?>                                        <?php else: ?>                                            <a href="#popup-into">Подать заявку для выступления</a>                                        <?php endif; ?>                                    <?php endif; ?>                                </div>                                <?php if(isset($_COOKIE['admin'])): ?>                                <div class="button">                                    <a href="#popup-change-competition<?= $this->arrCurrentCompetitions[$i]['id_competition'] ?>">Редактировать данные</a>                                </div>                                <?php endif; ?>                            </div>                        </div>                    </div>                <?php endfor; ?>                <?php endif; ?>            </div>        </div>        <div class="content__pagination">            <div class="content__pagination-body">                <div class="content__pagination-item"><a href="http://powerlift/?pag=<?= $this->prevPage ?>"><i class="icon-arrow-left"></i></a></div>                <? for($i = 1; $i <= $this->amountPages; $i++): ?>                    <div class="content__pagination-item"><a href="http://powerlift/?pag=<?= $i ?>"><?= $i ?></a></div>                <? endfor; ?>                <div class="content__pagination-item"><a href="http://powerlift/?pag=<?= $this->nextPage ?>"><i class="icon-arrow-right"></i></a></div>            </div>        </div>    </div>